package net.doge.sdk.service.music.info.lyrichero.qq.decoder;

import net.doge.sdk.service.music.info.lyrichero.qq.helper.QrcDecodeHelper;
import net.doge.util.collection.ArrayUtil;
import net.doge.util.core.CryptoUtil;

import java.nio.charset.StandardCharsets;
import java.util.Arrays;

/**
 * @Author Doge
 * @Description QRC 歌词解析工具类
 * @Date 2020/12/15
 */
public class QrcDecoder {
    private static QrcDecoder instance;

    private QrcDecoder() {
    }

    public static QrcDecoder getInstance() {
        if (instance == null) instance = new QrcDecoder();
        return instance;
    }

    private final short[] KEY1 = ArrayUtil.bytesToShorts("!@#)(NHLiuy*$%^&".getBytes(StandardCharsets.UTF_8));
    private final short[] KEY2 = ArrayUtil.bytesToShorts("123ZXC!@#)(*$%^&".getBytes(StandardCharsets.UTF_8));
    private final short[] KEY3 = ArrayUtil.bytesToShorts("!@#)(*$%^&abcDEF".getBytes(StandardCharsets.UTF_8));
    private QrcDecodeHelper qrcDecodeHelper = QrcDecodeHelper.getInstance();

    private void des(short[] data, short[] key, int len) {
        short[][] schedule = new short[16][6];
        qrcDecodeHelper.desKeySetup(key, schedule, QrcDecodeHelper.ENCRYPT);
        for (int i = 0; i < len; i += 8) {
            short[] in = Arrays.copyOfRange(data, i, data.length);
            qrcDecodeHelper.desCrypt(in, in, schedule);
            System.arraycopy(in, 0, data, i, in.length);
        }
    }

    private void ddes(short[] data, short[] key, int len) {
        short[][] schedule = new short[16][6];
        qrcDecodeHelper.desKeySetup(key, schedule, QrcDecodeHelper.DECRYPT);
        for (int i = 0; i < len; i += 8) {
            short[] in = Arrays.copyOfRange(data, i, data.length);
            qrcDecodeHelper.desCrypt(in, in, schedule);
            System.arraycopy(in, 0, data, i, in.length);
        }
    }

    public String decode(String hex) {
        try {
            short[] data = ArrayUtil.bytesToShorts(CryptoUtil.hexToBytes(hex));
            int dataLen = data.length;

            ddes(data, KEY1, dataLen);
            des(data, KEY2, dataLen);
            ddes(data, KEY3, dataLen);

            String result = new String(CryptoUtil.decompress(ArrayUtil.shortsToBytes(data)), StandardCharsets.UTF_8);
            return result;
        } catch (Exception e) {
            return "";
        }
    }

//    public static void main(String[] args) {
//        System.out.println(getInstance().decode(""));
//    }
}
